@import url("https://fonts.googleapis.com/css2?family=Poppins:wght@400;700&display=swap");

*{
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: "Poppins",sans-serif;
}

.main {
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100vh;
    width: 100%;
    background: rgba(187, 219, 255, 0.692);
}

.left {
    height: 550px;
    width: 300px;
    background: #000;
    box-shadow: 2px 2px 2px 5px rgba(0,0,0,0.1);
}

img {
    /* background: green; */
    margin-top: 100px;
    height: 450px;  
    width: 300px; 
}

.right{
    height: 450px;
    width: 350px;
    background: #fff;
}

.right h1 {
    padding: 18px 12px 0 24px;
}

.right h2 {
    padding: 0 12px 0 24px;
    color: rgb(85, 85, 85);
}

.right h4 {
    padding: 0 12px 12px 24px;
    color: rgb(68, 206, 68);
}

.right p {
    width: 310px;
    padding: 0px 12px 0 24px ;
}

.size {
    padding: 18px 12px 0 24px;
}

input[type="radio"][name="size"] {
  margin-right: 2px;
}

.color input[type="radio"] {
  appearance: none;              /* remove default browser look */
  -webkit-appearance: none;      /* for Safari */
  -moz-appearance: none;         /* for Firefox */
  width: 25px;
  height: 25px;
  border-radius: 50%;            /* make it a circle */
  border: 2px solid #ccc;        /* border around circle */
  cursor: pointer;
  margin: 5px;
  outline: none;
  transition: 0.2s ease;
}

/* custom colors by value */
.color input[value="red"] {
  background: red;
}

.color input[value="blue"] {
  background: blue;
}

.color input[value="green"] {
  background: green;
}

/* when selected, show inner dot (radio effect) */
.color input[type="radio"]:checked {
  box-shadow: 0 0 0 4px white inset, 0 0 0 2px black;
}

